import PopupWithForm from "./PopupWithForm";
import {useEffect} from "react";

const EditAvatarPopup = ({isOpen, onClose, onUpdateAvatar}) => {
    const avatarData = {avatar: ''}
    const {values, handleChange, errors, isValid, setValues, resetForm} = useFormAndValidation(avatarData)

    useEffect(() => {
        setValues(avatarData)
        if (!isOpen) {
            resetForm()
        }
    }, [isOpen])

    function handleSubmit(e) {
        e.preventDefault();
        onUpdateAvatar({
            avatar: values,
        })
    }

    return (
<PopupWithForm
title={"Обновить аватар"}
name={"avatar"}
isOpen={isOpen}
onClose={onClose}
buttonText="Сохранить"
>
<input
  required
  placeholder="Ссылка на новый аватар"
  id="avatarLink"
  type="url"
  className="popup__input popup__input_link_avatar"
  name="linkInput"
  minLength="2"
/>
<span className="popup__input-error" id="avatarLink-error"></span>
</PopupWithForm>
)}

    export default EditAvatarPopup;
