import PopupWithForm from "./PopupWithForm";
import { useEffect} from "react";
import { useForm } from "../hooks/useForm";

const EditAvatarPopup = ({ isOpen, onClose, onUpdateAvatar }) => {
  const avatarData = { linkInput: "" };
  const { values, setValues, handleChange } = useForm(avatarData)

  useEffect(() => {
    setValues(avatarData)
  }, [isOpen]);

  function handleSubmit(e) {
    e.preventDefault();
    onUpdateAvatar({
      avatar: values,
    });
  }

  // function handleChange(e) {
  //   setAvatar(e.target.value);
  // }

  return (
    <PopupWithForm
      title={"Обновить аватар"}
      name={"avatar"}
      isOpen={isOpen}
      onClose={onClose}
      onSubmit={handleSubmit}
      buttonText="Сохранить"
    >
      <input
        required
        placeholder="Ссылка на новый аватар"
        id="avatarLink"
        type="url"
        className="popup__input popup__input_link_avatar"
        name="linkInput"
        minLength="2"
        onChange={handleChange}
        value={values.linkInput || ''}
      />
      <span className="popup__input-error" id="avatarLink-error"></span>
    </PopupWithForm>
  );
};

export default EditAvatarPopup;
