import './App.css';
import '../index.css'
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithImage from './PopupWithImage';
import PopupWithForm from './PopupWithForm';
import {useState} from "react";


function App() {

    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
    const [selectedCard, setSelectedCard] = useState({});
    const [isImageOpen, setIsImageOpen] = useState(false);

    function handleCardClick(obj) {
        setIsImageOpen(true);
        setSelectedCard(obj);
    }

    function closeAllPopups() {
        setIsEditProfilePopupOpen(false);
        setIsAddPlacePopupOpen(false);
        setIsEditAvatarPopupOpen(false);
        setIsImageOpen(false);
    }

    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(true);
    }

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(true);
    }

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(true);
    }

  return (
    <div className="App">
        <div className="page">
            <Header />
            <Main
                onAddPlace={handleAddPlaceClick}
                onEditProfile={handleEditProfileClick}
                onEditAvatar={handleEditAvatarClick}
                onCardClick={handleCardClick}
                closePopup={closeAllPopups}
                // cards={cards}
                // onCardLike={handleCardLike}
                // onCardDelete={handleDeleting}
            />
            <Footer />
            <PopupWithImage onClose={closeAllPopups} card={selectedCard} isImageOpen={isImageOpen} />
            <PopupWithForm title={'Редактировать профиль'} name={'profile'} isOpen={isEditProfilePopupOpen} onClose={closeAllPopups}>
                <form action="#" className="popup__form" name="formElementProfile" id="editForm"  novalidate>
                    <input required placeholder="Имя" id="name" type="text" className="popup__input popup__input_field_name" name="nameInput" minlength="2" maxlength="40"/>
                    <span className="popup__input-error" id="name-error"></span>
                    <input required placeholder = "Деятельность" id="job" type="text" className="popup__input popup__input_field_job" name="jobInput" minlength="2" maxlength="200"/>
                    <span className="popup__input-error" id="job-error"></span>
                    <button type="submit" className="popup__submit" id="editSubmit">Сохранить</button>
                </form>
            </PopupWithForm>
            <PopupWithForm title={'Новое место'} name={'place'} isOpen={isAddPlacePopupOpen} onClose={closeAllPopups}>
                <form action="#" className="popup__form" name="formElementCard" id="addForm" novalidate>
                    <input required placeholder = "Название" id="place" type="text" className="popup__input popup__input_place_name" name="placeNameInput" minlength="2" maxlength="30"/>
                    <span className="popup__input-error" id="place-error"></span>
                    <input required placeholder = "Ссылка на картинку" id="link" type="url" className="popup__input popup__input_place_link" name="placeLinkInput" minlength="2"/>
                    <span className="popup__input-error" id="link-error"></span>
                    <button type="submit" className="popup__submit" id="addSubmit">Создать</button>
                </form>
            </PopupWithForm>
            <PopupWithForm title={'Вы уверены?'} name={'confirmation'}>
                <form action="#" className="popup__form" name="formElementProfile" id="confirmationForm"  novalidate>
                        <button type="submit" className="popup__submit" id="confirmationSubmit">Да</button>
                </form>
            </PopupWithForm>
            <PopupWithForm title={'Обновить аватар'} name={'avatar'} isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups}>
                <form action="#" class="popup__form" name="formElementProfile" id="editAvatar"  novalidate>
                        <input required placeholder="Ссылка на новый аватар" id="avatarLink" type="url" class="popup__input popup__input_link_avatar" name="linkInput" minlength="2"/>
                        <span class="popup__input-error" id="avatarLink-error"></span>
                        <button type="submit" class="popup__submit" id="avatarSubmit">Сохранить</button>
                </form>
            </PopupWithForm>
        </div>
    </div>
  );
};
export default App;


