import React from 'react';
import "../index";

const PopupWithForm = ({ title, name, children, isOpen, onClose }) => {

    return (
        <div className={`popup ${isOpen ? 'popup_opened' : ''}`}  id={`popup_${name}`}>
            <div className="popup__container">
                <button className="popup__close" type="button" id={`${name}_close`} onClick={onClose}/>
                <h2 className="popup__title">{title}</h2>
                <form name={`${name}_form`} id={`${name}_form`}>
                    {children}
                    {/* <button className="popup__submit" type="submit" disabled>Сохранить
                    </button> */}
                </form>
            </div>
        </div>
    );
};

export default PopupWithForm;
