import "../index";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithImage from "./PopupWithImage";
import PopupWithForm from "./PopupWithForm";
import api from "../utils/Api";
import { useState, useEffect } from "react";
import CurrentUserContext  from "../context/CurrentUserContext";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [isImageOpen, setIsImageOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);

  useEffect(() => {
    api.getCards()
      .then((initialCards) => {
        setCards(initialCards);
      })
      .catch((err) => {
        console.log(err);
      });
}, []);

function handleAddCard(cardData) {
  setIsLoadingAddCard(true)
  api.createCard(cardData.values).then(newCard => {
      setCards([newCard, ...cards])
      closeAllPopup()
  }).finally(() => {
      setIsLoadingAddCard(false)
  }).catch(e => console.log(e))
}

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    api.changeLikeCardStatus(card._id, isLiked)
    .then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
  }  

  function handleCardDelete(card) {
    api.deleteCard(card._id)
    .then(setCards(cards.filter(item => item._id !== card._id)))
    .catch((err) => console.log(err))
  };

  function handleCardClick(obj) {
    setIsImageOpen(true);
    setSelectedCard(obj);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsImageOpen(false);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }


  useEffect(() => {
    api.getUserInfo()
      .then(userData => {
        setCurrentUser(userData)})
      .catch(err => console.log(err))
}, [])

function handleUpdateUser(userInfo) {
  api
  .editUserInfo(userInfo.values).then(newUserData => {
      setCurrentUser(newUserData);
      closeAllPopups();
  })
  .catch(e => console.log(e))
}

function handleUpdateAvatar(avatarData) {
  api.setAvatar(avatarData.avatar)
    .then(newUserAvatar => {
      setCurrentUser(newUserAvatar)
      closeAllPopups()
    })
    .catch(e => console.log(e))
}

  return (<CurrentUserContext.Provider value={currentUser}>
      <div className="App">
        <div className="page">
          <Header />
          <Main
            onAddPlace={handleAddPlaceClick}
            onEditProfile={handleEditProfileClick}
            onEditAvatar={handleEditAvatarClick}
            onCardClick={handleCardClick}
            cards={cards}
            onCardLike={handleCardLike}
            onCardDelete = {handleCardDelete}

          />
          <Footer />
          <PopupWithImage
            onClose={closeAllPopups}
            card={selectedCard}
            isImageOpen={isImageOpen}
          />
          <EditProfilePopup 
            isOpen={isEditProfilePopupOpen} 
            onClose={closeAllPopups} 
            onUpdateUser={handleUpdateUser} 
            /> 
          <AddPlacePopup 
            isOpen={isAddPlacePopupOpen} 
            onClose={closeAllPopups} 
            onUpdateUser={handleUpdateUser} 
            /> 
          <PopupWithForm title={"Вы уверены?"} name={"confirmation"}  buttonText="Да">
          </PopupWithForm>
          <EditAvatarPopup 
            isOpen={isEditAvatarPopupOpen} 
            onClose={closeAllPopups}
            onUpdateAvatar= {handleUpdateAvatar}
            />
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}
export default App;
