import "../index";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithImage from "./PopupWithImage";
import PopupWithForm from "./PopupWithForm";
import api from "../utils/Api";
import { useState, useEffect } from "react";
import CurrentUserContext  from "../context/CurrentUserContext";
import EditProfilePopup from "./EditProfilePopup";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [isImageOpen, setIsImageOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState({});
  const [isLoadingUser, setIsLoadingUser] = useState(false);

  function handleCardClick(obj) {
    setIsImageOpen(true);
    setSelectedCard(obj);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsImageOpen(false);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleUpdateUser(userInfo) {
    setIsLoadingUser(true)
    api
    .setUserInfo(userInfo.values).then(newUserData => {
        setCurrentUser(newUserData)
        closeAllPopup()
    })
    .finally(() => setIsLoadingUser(false)).catch(e => console.log(e))
}

  useEffect(() => {
    api.getUserInfo()
      .then(userData => {
        setCurrentUser(userData)})
      .catch(err => console.log(err))
}, [])

  return (<CurrentUserContext.Provider value={currentUser}>
      <div className="App">
        <div className="page">
          <Header />
          <Main
            onAddPlace={handleAddPlaceClick}
            onEditProfile={handleEditProfileClick}
            onEditAvatar={handleEditAvatarClick}
            onCardClick={handleCardClick}
            // cards={cards}
            // handleCardLike={handleCardLike}
          />
          <Footer />
          <PopupWithImage
            onClose={closeAllPopups}
            card={selectedCard}
            isImageOpen={isImageOpen}
          />
          <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} isLoading={isLoadingUser}/> 
          <PopupWithForm
            title={"Новое место"}
            name={"place"}
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            buttonText="Cоздать"
          >
            <input
              required
              placeholder="Название"
              id="place"
              type="text"
              className="popup__input popup__input_place_name"
              name="placeNameInput"
              minLength="2"
              maxLength="30"
            />
            <span className="popup__input-error" id="place-error"></span>
            <input
              required
              placeholder="Ссылка на картинку"
              id="link"
              type="url"
              className="popup__input popup__input_place_link"
              name="placeLinkInput"
              minLength="2"
            />
            <span className="popup__input-error" id="link-error"></span>
          </PopupWithForm>
          <PopupWithForm title={"Вы уверены?"} name={"confirmation"}  buttonText="Да">
          </PopupWithForm>
          <PopupWithForm
            title={"Обновить аватар"}
            name={"avatar"}
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            buttonText="Сохранить"
          >
            <input
              required
              placeholder="Ссылка на новый аватар"
              id="avatarLink"
              type="url"
              className="popup__input popup__input_link_avatar"
              name="linkInput"
              minLength="2"
            />
            <span className="popup__input-error" id="avatarLink-error"></span>
          </PopupWithForm>
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}
export default App;
